import React, { Component } from "react";

class GameScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      x: 50,
      y: 50
    }
  }

  componentDidMount() {
    console.log('hello');
    document.addEventListener("keydown", this.handleKeyDown);
  }

  componentWillUnmount() {
    document.removeEventListener("keydown", this.handleKeyDown);
  }

  handleKeyDown = (event) => {
    console.log(event.key);
    let x;
    let y;
    switch(event.key) {
      case("a"):
        x = this.state.x - 1;
        this.setState({x})
        break;
      case("w"):
        y = this.state.y + 1;
        this.setState({y})
        break;
      case("s"):
        y = this.state.y - 1;
        this.setState({y})
        break;
      case("d"):
        x = this.state.x + 1;
        this.setState({x})
        break;
    }
  }
  render() {
    return (
      <div className="GameScreen" style={{position:'relative'}}>
        <p>Game</p>
        <p>x: {this.state.x}</p>
        <p>y: {this.state.y}</p>
        <div style={{position: 'absolute', left: `${this.state.x}%`, width: '100px'}}>
          <p>Me!</p>
        </div>
      </div>
    );
  }
}

export default GameScreen;
